                 CROSS apply (SELECT ID 
                              FROM   mm.ALL_OutcomeMenuCategory O2MC 
                              WHERE  Title = 'Projects'
							  AND O2MC.Outcome_ID = OC.Outcome_ID
							  ) Src 
          WHERE  Outcome_ID = dp.Outcome_ID 
