            And TABLE_SCHEMA = ''' + @@TableSchema + ''' 
           AND  COLUMN_NAME = ''sys_CreatedOn'') ALTER TABLE ' + @@TableSchema + '.' + @@Table +' ADD sys_CreatedOn DateTime  Null  
